import React from "react";
import { connect } from "react-redux";

const CheckoutPage = (props) => {
  console.log(props);
  return (
    <>
      <h3>Checkout Page</h3>
      <div
        style={{
          width: "40%",
          marginLeft: "30px",
          height: "350px",
          backgroundColor: "white",
          padding: "10px",
        }}
      >
        <h4>Your order</h4>
        <table
          width="100%"
          rules="rows"
          style={{
            width: "100%",
            height: "200px",
            borderBottom: "1px solid",
          }}
        >
          <thead>
            <tr>
              <th>Product</th>
              <th>Subtotal</th>
            </tr>
          </thead>
          <tbody style={{ fontSize: "1.2em" }}>
            {props.cart.map((item) => (
              <tr key={item.id}>
                <td>
                  {item.title} x {item.quantity}
                </td>
                <td>${item.cost}</td>
              </tr>
            ))}
          </tbody>
          <tfoot>
            <tr>
              <th>Subtotal</th>
              <td>
                $
                {props.cart.reduce(
                  (sum, current) => sum + parseInt(current.cost),
                  0
                )}
              </td>
            </tr>
            <tr>
              <th>Shipping</th>
              <td>
                <span>DHL or Fedex (+ $17)</span>
              </td>
            </tr>
          </tfoot>
        </table>
      </div>
    </>
  );
};

function mapStateToProps(state, ownProps) {
  return {
    cart: state.cart,
  };
}

export default connect(mapStateToProps)(CheckoutPage);
