import React from "react";
import { connect } from "react-redux";
import {
  loadCart,
  deleteItemFromCart,
  updateCart,
} from "../../redux/actions/cartActions";
import CardForCart from "./CardForCart";
import { Table } from "antd";
import { dataSource, columns } from "./TableCartWithInfo";

class CartPage extends React.Component {
  constructor(props) {
    super(props);
    this.state = { cart: [], totalCost: 0 };

    // this.handleDeleteFromCart = this.handleDeleteFromCart.bind(this);
    this.componentCleanup = this.componentCleanup.bind(this);
  }

  componentCleanup() {
    this.props.updateCart(this.state.cart);
  }

  async componentDidMount() {
    await this.props.loadCart().catch((error) => console.error(error));
    this.setState({ cart: this.props.cart });
    window.addEventListener("beforeunload", this.componentCleanup);
  }

  componentWillUnmount() {
    this.componentCleanup();
    window.removeEventListener("beforeunload", this.componentCleanup);
    // this.props.updateCart(this.state.cart);
  }

  componentDidUpdate() {}

  handleChangeQuantity = (item, changedValue) => {
    const updatedCart = this.state.cart.map((i) => {
      if (i.id === item.id) {
        return {
          ...item,
          quantity: changedValue,
          cost: parseInt(item.price) * changedValue,
        };
      }
      return i;
    });
    this.setState({ cart: updatedCart });

    // if (isNaN(changedValue)) {
    //   // console.log({ ...item, quantity: 1 });
    //   const updatedCart = this.state.cart.map((i) => {
    //     if (i.id === item.id) {
    //       return { ...item, quantity: 1, cost: parseInt(item.price) * 1 };
    //     }
    //     return i;
    //   });
    //   this.setState({ cart: updatedCart });
    // } else {
    //   // console.log({ ...item, quantity: changedValue });
    //   const updatedCart = this.state.cart.map((i) => {
    //     if (i.id === item.id) {
    //       return {
    //         ...item,
    //         quantity: changedValue,
    //         cost: parseInt(item.price) * changedValue,
    //       };
    //     }
    //     return i;
    //   });
    //   this.setState({ cart: updatedCart });
    // }
  };

  handleDeleteItem(item) {
    this.setState({ cart: this.state.cart.filter((i) => i.id !== item.id) });
    this.props.deleteItemFromCart(item);
  }

  UNSAFE_componentWillReceiveProps(nextProps) {
    this.setState({ cart: nextProps.cart });
  }

  render() {
    // const listOfCarts = this.state.cart.map((item) => (
    //   <CardForCart
    //     key={item.id}
    //     id={item.id}
    //     path={item.path}
    //     title={item.title}
    //     cost={item.cost}
    //     onDeleteItem={() => this.handleDeleteItem(item)}
    //     onChangeQuantity={(count) => this.handleChangeQuantity(item, count)}
    //     quantity={item.quantity}
    //   />
    // ));

    // const totalCost = listOfCarts.reduce(
    //   (sum, current) => sum + parseInt(current.props.cost),
    //   0
    // );

    const totalCost = this.state.cart.reduce(
      (sum, current) => sum + parseInt(current.cost),
      0
    );

    return (
      <div>
        <h1>Cart Page</h1>
        <section
          style={{
            display: "flex",
            justifyContent: "center",
            marginLeft: "15%",
            marginRight: "15%",
          }}
        >
          <div
            style={{
              display: "flex",
              flexDirection: "column",
              width: "60%",
            }}
          >
            {this.state.cart.map((item) => (
              <CardForCart
                key={item.id}
                id={item.id}
                path={item.path}
                title={item.title}
                cost={item.cost}
                onDeleteItem={() => this.handleDeleteItem(item)}
                onChangeQuantity={(count) =>
                  this.handleChangeQuantity(item, count)
                }
                quantity={item.quantity}
              />
            ))}
            <h2 style={{ alignSelf: "flex-end" }}>Subtotal: ${totalCost}</h2>
          </div>
          <div style={{ width: "40%", marginLeft: "30px", height: "350px" }}>
            <h3>Cart totals</h3>
            <table width="100%" height="100%" rules="rows">
              <tbody>
                <tr>
                  <th>Subtotal</th>
                  <td>$5684</td>
                </tr>
                <tr>
                  <th>Shipping</th>
                  <td>DHL or Fedex (+ $17)</td>
                </tr>
                <tr>
                  <th>State Tax 13%</th>
                  <td>$652</td>
                </tr>
                <tr style={{ font: "5em" }}>
                  {" "}
                  <th>Total</th>
                  <td>$6525</td>
                </tr>
              </tbody>
            </table>
          </div>

          {/* <Table
            style={{ width: "40%", marginLeft: "10px" }}
            dataSource={dataSource}
            columns={columns}
            pagination={false}
          /> */}
        </section>
      </div>
    );
  }
}

function mapStateToProps(state, ownProps) {
  return {
    cart: state.cart,
  };
}

const mapDispatchToProps = {
  loadCart,
  deleteItemFromCart,
  updateCart,
};

export default connect(mapStateToProps, mapDispatchToProps)(CartPage);
