import React, { useEffect, useState } from "react";
import { NavLink } from "react-router-dom";
import { withRouter } from "react-router";
import { connect } from "react-redux";
import { Layout, Menu, Dropdown } from "antd";
import MenuForHeader from "../common/MenuForHeader";
import { ShoppingCartOutlined } from "@ant-design/icons";
import { loadApple } from "../../redux/actions/appleActions";
import { loadCart } from "../../redux/actions/cartActions";
// import CardForCart from "../cart/CardForCart";
import { DeleteOutlined } from "@ant-design/icons";
import { Button } from "antd";

const HeaderAntd = Layout.Header;

function Header(props) {
  const [quantity, setQuantity] = useState(0);
  const [visible, setVisible] = useState(false);
  const [cart, setCart] = useState([]);

  useEffect(() => {
    props.loadApple();
    props.loadCart();
  }, []);

  useEffect(() => {
    if (props.cart.length > 0) {
      const totalItemsQuantity = props.cart.reduce(
        (sum, current) => sum + parseInt(current.quantity),
        0
      );
      setQuantity(totalItemsQuantity);
    }

    if (props.cart.length === 0) {
      setQuantity(0);
    }
  }, [props]);

  useEffect(() => {
    if (props.cart.length > 0) {
      setCart(props.cart);
    }
  }, [props]);

  console.log(cart);
  return (
    <HeaderAntd
      style={{
        zIndex: 999,
        width: "100%",
        position: "fixed",
        fontFamily: "Helvetica",
        display: "flex",
        justifyContent: "space-between",
        alignItems: "center",
      }}
    >
      <MenuForHeader />

      <Dropdown
        visible={visible}
        onVisibleChange={(flag) => setVisible(flag)}
        overlay={() => (
          <ul
            style={{
              flex: "1 1 auto",
              overflowY: "auto",
              minHeight: "0px",
              padding: "0",
              maxHeight: "400px",
              border: "2px solid #f0f2f5",
              borderBottom: "none",
              width: "400px",
              backgroundColor: "white",
              zIndex: 9999,
              display: "flex",
              flexDirection: "column",
            }}
          >
            {cart.map((item) => (
              <li
                key={item.id}
                style={{
                  display: "flex",
                  justifyContent: "space-between",
                  alignItems: "center",
                  borderBottom: "2px solid #f0f2f5",
                }}
              >
                <img width="150px" src={item.path}></img>
                <div>
                  <div>{item.title}</div>
                  <div>
                    {item.quantity} x ${item.price}
                  </div>
                </div>
                <Button style={{ marginRight: "5%" }}>
                  <DeleteOutlined />
                </Button>
              </li>
            ))}
          </ul>
        )}
      >
        <NavLink to="/cart">
          <div>
            {quantity}
            <ShoppingCartOutlined
              style={{ fontSize: "170%", color: "rgba(255, 255, 255, 0.65)" }}
            />
          </div>
        </NavLink>
      </Dropdown>
    </HeaderAntd>
  );
}

const mapStateToProps = (state, ownProps) => ({
  cart: state.cart,
});

const mapDispatchToProps = {
  loadCart,
  loadApple,
};

export default connect(mapStateToProps, mapDispatchToProps)(withRouter(Header));
