import React from "react";
import { connect } from "react-redux";
import { Form, Input, InputNumber, Button } from "antd";

const layout = {
  labelCol: {
    span: 8,
  },
  wrapperCol: {
    span: 16,
  },
};
const validateMessages = {
  required: "${label} is required!",
  types: {
    email: "${label} is not validate email!",
    number: "${label} is not a validate number!",
  },
  number: {
    range: "${label} must be between ${min} and ${max}",
  },
};

const CheckoutPage = (props) => {
  const onFinish = (values) => {
    console.log(values);
  };
  console.log(props);
  const subtotal = props.cart.reduce(
    (sum, current) => sum + parseInt(current.cost),
    0
  );
  const stateTax = (subtotal * 0.09).toFixed(2);
  return (
    <>
      <Form
        {...layout}
        name="nest-messages"
        onFinish={onFinish}
        validateMessages={validateMessages}
      >
        <Form.Item
          name={["user", "name"]}
          label="Name"
          rules={[
            {
              required: true,
            },
          ]}
        >
          <Input />
        </Form.Item>
        <Form.Item
          name={["user", "email"]}
          label="Email"
          rules={[
            {
              type: "email",
            },
          ]}
        >
          <Input />
        </Form.Item>
        <Form.Item
          name={["user", "age"]}
          label="Age"
          rules={[
            {
              type: "number",
              min: 0,
              max: 99,
            },
          ]}
        >
          <InputNumber />
        </Form.Item>
        <Form.Item name={["user", "website"]} label="Website">
          <Input />
        </Form.Item>
        <Form.Item name={["user", "introduction"]} label="Introduction">
          <Input.TextArea />
        </Form.Item>
        <Form.Item wrapperCol={{ ...layout.wrapperCol, offset: 8 }}>
          <Button type="primary" htmlType="submit">
            Submit
          </Button>
        </Form.Item>
      </Form>

      <h3>Checkout Page</h3>
      {subtotal === 0 ? (
        <h5
          style={{
            backgroundColor: "white",
            height: "100px",
            paddingTop: "1%",
            marginRight: "70%",
          }}
        >
          Your cart is currently empty.
        </h5>
      ) : (
        <div
          style={{
            width: "40%",
            marginLeft: "30px",
            height: "450px",
            backgroundColor: "white",
            padding: "10px",
          }}
        >
          <h4>Your order</h4>
          <table
            width="100%"
            rules="rows"
            style={{
              width: "100%",
              height: "330px",
              borderBottom: "1px solid",
            }}
          >
            <thead>
              <tr>
                <th>Product</th>
                <th>Subtotal</th>
              </tr>
            </thead>
            <tbody style={{ fontSize: "1.2em" }}>
              {props.cart.map((item) => (
                <tr key={item.id}>
                  <td>
                    {item.title} x {item.quantity}
                  </td>
                  <td>${item.cost}</td>
                </tr>
              ))}
            </tbody>
            <tfoot style={{ fontSize: "1.3em", fontWeight: "bold" }}>
              <tr>
                <th>Subtotal</th>
                <td>
                  <span>${subtotal}</span>
                </td>
              </tr>

              <tr>
                <th>Ohio State Tax 9%</th>
                <td>
                  <span>${parseFloat(stateTax).toFixed(2)}</span>
                </td>
              </tr>
              <tr>
                <th>Shipping</th>
                <td>
                  <span>DHL or Fedex (+ $17)</span>
                </td>
              </tr>
              <tr>
                <th>Total</th>
                <td>
                  <span>
                    ${(subtotal + parseFloat(stateTax) + 17).toFixed(2)}
                  </span>
                </td>
              </tr>
            </tfoot>
          </table>
        </div>
      )}
    </>
  );
};

function mapStateToProps(state, ownProps) {
  return {
    cart: state.cart,
  };
}

export default connect(mapStateToProps)(CheckoutPage);
