import React from "react";
import { connect } from "react-redux";

const CheckoutPage = (props) => {
  console.log(props);
  const subtotal = props.cart.reduce(
    (sum, current) => sum + parseInt(current.cost),
    0
  );
  const stateTax = (subtotal * 0.09).toFixed(2);
  return (
    <>
      <h3>Checkout Page</h3>
      {subtotal === 0 ? (
        <h5 style={{ backgroundColor: "white" }}>
          Your cart is currently empty.
        </h5>
      ) : (
        <div
          style={{
            width: "40%",
            marginLeft: "30px",
            height: "450px",
            backgroundColor: "white",
            padding: "10px",
          }}
        >
          <h4>Your order</h4>
          <table
            width="100%"
            rules="rows"
            style={{
              width: "100%",
              height: "330px",
              borderBottom: "1px solid",
            }}
          >
            <thead>
              <tr>
                <th>Product</th>
                <th>Subtotal</th>
              </tr>
            </thead>
            <tbody style={{ fontSize: "1.2em" }}>
              {props.cart.map((item) => (
                <tr key={item.id}>
                  <td>
                    {item.title} x {item.quantity}
                  </td>
                  <td>${item.cost}</td>
                </tr>
              ))}
            </tbody>
            <tfoot style={{ fontSize: "1.3em", fontWeight: "bold" }}>
              <tr>
                <th>Subtotal</th>
                <td>
                  <span>${subtotal}</span>
                </td>
              </tr>

              <tr>
                <th>Ohio State Tax 9%</th>
                <td>
                  <span>${parseFloat(stateTax).toFixed(2)}</span>
                </td>
              </tr>
              <tr>
                <th>Shipping</th>
                <td>
                  <span>DHL or Fedex (+ $17)</span>
                </td>
              </tr>
              <tr>
                <th>Total</th>
                <td>
                  <span>
                    ${(subtotal + parseFloat(stateTax) + 17).toFixed(2)}
                  </span>
                </td>
              </tr>
            </tfoot>
          </table>
        </div>
      )}
    </>
  );
};

function mapStateToProps(state, ownProps) {
  return {
    cart: state.cart,
  };
}

export default connect(mapStateToProps)(CheckoutPage);
